import React, {userState} from 'react'
import {HiBell} from "react-icons/hi";


const PinImage = (props) => {
    // Estado para el hover de la imagen
    const [hover, setHover] = useState('none');
  
    // Estos son los estilos propios de la imagen
    const _stylesPinImage = {
      pin_image_card: {
        margin: 0,
        padding: 0,
        display: 'grid',
        gridTemplateColumns: '1fr',
      },
      small: {
        gridRowEnd: 'span 26',
      },
      medium: {
        gridRowEnd: 'span 33',
      },
      large: {
        gridRowEnd: 'span 45',
      },
      image: {
        margin: 0,
        padding: 0,
        height: '100%',
        width: '100%',
        borderRadius: '16px',
        aspectRatio: 1,
        gridColumn: '1/2',
        gridRow: '1/2',
        zIndex: 0,
      },
      hoverPinImage: {
        width: '100%',
        height: '100%',
        gridColumn: '1/2',
        gridRow: '1/2',
        zIndex: 1,
      },
    };
    return (
        <HiBell style={_styleHiBell.iconHiBell}/>
)
  };
  
  export default PinImage;
