import React, { useState } from 'react';
import {AiFillMessage} from "react-icons/ai";

const _styleAiFillMessage = {
    iconAiFillMessage:{
        width: '25px',
        height: '25px',
        borderRadius: '33px',
        color: '#767676',
        cursor: 'pointer',
    },
    HiBellHover:{
        padding: '5px',
        backgroundColor: `${bgColour}`,
        borderRadius: '50%',
        cursor: 'pointer',
    }
};

function PinMessageUser() {
    const [bgColour, setBgColour] = useState("#767676")
    
    return (
        <div style={_styleHiBell.HiBellHover}         
        onMouseEnter={() => setBgColour("#efefef")}
        onMouseLeave={() => setBgColour("")}>
            <AiFillMessage style={_styleAiFillMessage.iconAiFillMessage} />
        </div>

    )
}

export default PinMessageUser
