import React from 'react'
import {BiChevronDown} from "react-icons/bi";

const PinChevron = () => {
    // Estado para el hover de la imagen
    const [bgColour, setBgColour] = useState("#767676")
    
    // Estos son los estilos propios de la imagen
    const _styleChevron = {
        iconChevron:{
            width: '25px',
            height: '25px',
            borderRadius: '33px',
            color: '#767676',
            cursor: 'pointer',
        },
        ChevronHover:{
            padding: '5px',
            backgroundColor: `${bgColour}`,
            borderRadius: '50%',
            cursor: 'pointer',
        }
    };

function PinChevron() {
    return (
        <div>
             <BiChevronDown style={_styleChevron.iconChevron}/>
        </div>

       
    )
}

};
export default PinChevron;
