import React, { useState } from 'react';
import {AiFillMessage} from "react-icons/hi";


const PinM = () => {
    // Estado para el hover de la imagen
    const [bgColour, setBgColour] = useState("#767676")
    
    // Estos son los estilos propios de la imagen
    const _styleHiBell = {
        iconHiBell:{
            width: '25px',
            height: '25px',
            borderRadius: '33px',
            color: "#767676",
            cursor: 'pointer',
        },
        HiBellHover:{
            padding: '5px',
            backgroundColor: `${bgColour}`,
            borderRadius: '50%',
            cursor: 'pointer',
        }
    };

    return (
        <div  style={_styleHiBell.HiBellHover}         
        onMouseEnter={() => setBgColour("#efefef")}
        onMouseLeave={() => setBgColour("")}>
        <HiBell style={_styleHiBell.iconHiBell}
        />
        </div>

)
    };

    export default PinHiBellUser;
