import React, { useState } from 'react';
import {HiBell} from "react-icons/hi";


const PinHiBellUser = (props) => {
    // Estado para el hover de la imagen
    const [bgColour, setBgColour] = useState("#767676")
    
    // Estos son los estilos propios de la imagen
    const _styleHiBell = {
        iconHiBell:{
            width: '25px',
            height: '25px',
            borderRadius: '33px',
            color: `${bgColour}`,
            cursor: 'pointer',
        },
        HiBellHover:{
            padding: '10px',
            backgroundColor: `${bgColour}`,
        }
    };

    return (
        <div  style={_styleHiBell.HiBellHover}         
        onMouseEnter={() => setBgColour("#c83f49")}
        onMouseLeave={() => setBgColour("#767676")}>
        <HiBell style={_styleHiBell.iconHiBell}

        />
        </div>

)
    };

    export default PinHiBellUser;
